        val requestPresentation = json.decodeFromString<RequestPresentation>(requestPresentationJson)

        val agent = EdgeAgent(
            apollo = apolloMock,
            castor = castorMock,
            pluto = plutoMock,
            mercury = mercuryMock,
            pollux = polluxMock,
            connectionManager = connectionManagerMock,
            seed = seed,
            api = apiMock,
            logger = LoggerMock()
        )

        val credential = AnonCredential.fromStorableData(
            "".toByteArray()
