        val schemaJsonResponse =
            """{"name":"Driving licence Anoncred Schema","version":"1.0","attrNames":["emailaddress"],"issuerId":"did:prism:337e8fe144aaced3ca3dd59462404f45996e3b221abc410a725ea665348972bb"}"""

        `when`(
