                credential.schemaID,
                emptyArray(),
                arrayOf(KeyValue(HttpHeaders.ContentType, Typ.Encrypted.typ)),
                null
            )
        ).thenReturn(HttpResponse(200, schemaJsonResponse))

        val credentialDefinitionJsonResponse =
            """{"schemaId":"http://host.docker.internal:8000/prism-agent/schema-registry/schemas/f52ebc61-6146-3238-99c5-a3fd693b96f7/schema","type":"CL","tag":"automation-test","value":{"primary":{"n":"","s":"","r":{"emailaddress":"","master_secret":""},"rctxt":"","z":""}},"issuerId":"did:prism:337e8fe144aaced3ca3dd59462404f45996e3b221abc410a725ea665348972bb"}"""

