    @Test
    fun restoreFromKMP() = runTest {
        val realPluto = PlutoImpl(DbConnectionInMemory())
        edgeAgent = EdgeAgent(
            apollo,
            castor,
            realPluto,
            MercuryMock(),
            PolluxImpl(apollo, castor),
            seed,
            null,
            MediationHandlerMock()
        )

